class ED:

    def my_encoder(encd_str):
        try:
            encd_list = []
            for enc in encd_str:
                slice_enc = encryption(enc)
                encd_list.append(slice_enc)
            encd_list = list(reversed(encd_list))
            merge_str = ''.join(encd_list)
            return merge_str
        except:
            print("Wrong input!!")


    def my_decoder(encded_string):
        try:
            #encded_string = "^hGtb6uerer5tyds5:"
            encded_string_reversed = [encded_string[i:i+3] for i in range(0, len(encded_string), 3)]
            #print(encded_string)
            encded_string = list(reversed(encded_string_reversed))
            decd_list = []
            for dec in encded_string:
                slice_dec = (decryption(dec))
                decd_list.append(slice_dec)
            decd_string = ''.join(decd_list)
            return decd_string
        except:
            print("Wrong input!!")


