from ..nlit.tree import (TREE_SPACE, TREE_BRANCH, TREE_TEE, TREE_LAST)

# %% ../../nbs/04_enum/01_tree.ipynb 7
class TreeStr(StrEnum):
    SPACE  = TREE_SPACE
    BRANCH = TREE_BRANCH
    TEE    = TREE_TEE
    LAST   = TREE_LAST

    @classmethod
    def next(cls, curr: 'TreeStr') -> 'TreeStr': 
        return cls.BRANCH if curr == cls.TEE else cls.SPACE    
    
    @classmethod
    def join(cls, prev: Optional[str] = None, curr: Optional['TreeStr'] = None) -> str: 
        return f'{prev or ""}{cls.next(curr or cls.TEE)}'
